// Function to compute the last non-zero digit of the factorial of a given number n
fun computeLastNonZeroDigitOfFactorial(n: Int): Int {
    if (n == 0) return 1 // Factorial of 0 is 1

    var result = 1
    var count2 = 0
    var count5 = 0

    for (i in 1..n) {
        var num = i
        // Factor out all 2s and 5s from the current number
        while (num % 2 == 0) {
            count2++
            num /= 2
        }
        while (num % 5 == 0) {
            count5++
            num /= 5
        }
        // Multiply the remaining part to the result and keep only the last digit
        result = (result * num) % 10
    }

    // Handle excess 2s or 5s
    val excess = count2 - count5
    if (excess > 0) {
        for (i in 1..excess) {
            result = (result * 2) % 10
        }
    } else {
        for (i in 1..-excess) {
            result = (result * 5) % 10
        }
    }

    return result
}

