// analyzeEmails processes a list of email strings to count unique senders and recipients.
// Each email string is expected to contain a sender and recipient enclosed in angle brackets.
// The function returns a Pair<Int, Int> where the first element is the count of unique senders and the second is the count of unique recipients.
fun analyzeEmails(n: Int, emails: List<String>): Pair<Int, Int> {
    val senders = mutableSetOf<String>()
    val recipients = mutableSetOf<String>()

    for (email in emails) {
        val senderStart = email.indexOf('<') + 1
        val senderEnd = email.indexOf('>')
        val recipientStart = email.indexOf('<', senderEnd) + 1
        val recipientEnd = email.indexOf('>', recipientStart)

        val sender = email.substring(senderStart, senderEnd)
        val recipient = email.substring(recipientStart, recipientEnd)

        senders.add(sender)
        recipients.add(recipient)
    }

    return Pair(senders.size, recipients.size)
}

assertions:
fun testAnalyzeEmails() {
    // Test case 1: Two emails with unique senders and recipients
    val test1 = analyzeEmails(2, listOf(
        "From: <sender1@example.com> To: <recipient1@example.com>",
        "From: <sender2@example.com> To: <recipient2@example.com>"
    ))
    assert(test1.first == 2 && test1.second == 2)

    // Test case 2: Two emails with the same sender but different recipients
    val test2 = analyzeEmails(2, listOf(
        "From: <sender1@example.com> To: <recipient1@example.com>",
        "From: <sender1@example.com> To: <recipient2@example.com>"
    ))
    assert(test2.first == 1 && test2.second == 2)

    // Test case 3: Two emails with different senders but the same recipient
    val test3 = analyzeEmails(2, listOf(
        "From: <sender1@example.com> To: <recipient1@example.com>",
        "From: <sender2@example.com> To: <recipient1@example.com>"
    ))
    assert(test3.first == 2 && test3.second == 1)

    // Test case 4: Single email
    val test4 = analyzeEmails(1, listOf(
        "From: <sender1@example.com> To: <recipient1@example.com>"
    ))
    assert(test4.first == 1 && test4.second == 1)
}

testAnalyzeEmails()

