import java.util.PriorityQueue

// Determines if books can be reorganized so that no two books of the same genre are adjacent.
// 
// Parameters:
// books: List of pairs where each pair contains a bookID and a genre.
// 
// Returns:
// Boolean: true if books can be reorganized, otherwise false.
fun canReorganizeBooks(books: List<Pair<String, String>>): Boolean {
    if (books.isEmpty()) {
        return true
    }

    // Count frequency of each genre
    val genreCount = mutableMapOf<String, Int>()
    for ((_, genre) in books) {
        genreCount[genre] = genreCount.getOrDefault(genre, 0) + 1
    }

    // Convert counts to a max-heap using a priority queue
    val maxHeap = PriorityQueue<Pair<Int, String>>(compareByDescending { it.first })
    for ((genre, count) in genreCount) {
        maxHeap.add(Pair(count, genre))
    }

    var prevCount = 0
    var prevGenre: String? = null

    while (maxHeap.isNotEmpty()) {
        val (count, genre) = maxHeap.poll()

        // Place the previous genre back into the max heap
        if (prevCount > 0) {
            maxHeap.add(Pair(prevCount, prevGenre!!))
        }

        // Add the current genre to the result (hypothetical placement)
        prevCount = count - 1
        prevGenre = genre
    }

    return (prevCount == 0)
}

assertions:
fun testCanReorganizeBooks() {
    // Test case 1: Balanced genres
    val testCase1 = listOf(
        Pair("Book1", "Fiction"),
        Pair("Book2", "Fiction"),
        Pair("Book3", "NonFiction"),
        Pair("Book4", "NonFiction")
    )
    assert(canReorganizeBooks(testCase1) == true)

    // Test case 2: Unbalanced genres (3 Fiction, 1 NonFiction)
    val testCase2 = listOf(
        Pair("Book1", "Fiction"),
        Pair("Book2", "Fiction"),
        Pair("Book3", "Fiction"),
        Pair("Book4", "NonFiction")
    )
    assert(canReorganizeBooks(testCase2) == false)

    // Test case 3: Balanced genres (1 Fiction, 1 NonFiction, 2 SciFi)
    val testCase3 = listOf(
        Pair("Book1", "Fiction"),
        Pair("Book2", "NonFiction"),
        Pair("Book3", "SciFi"),
        Pair("Book4", "SciFi")
    )
    assert(canReorganizeBooks(testCase3) == true)

    // Test case 4: Empty list
    val testCase4 = listOf<Pair<String, String>>()
    assert(canReorganizeBooks(testCase4) == true)
}

testCanReorganizeBooks()

