// Function to generate all possible permutations of a tetrahedron's colors by rotating around each vertex
fun generatePermutations(tetra: List<Int>): Set<List<Int>> {
    val permutations = mutableSetOf<List<Int>>()
    for (i in 0 until 4) {
        val adj = when (i) {
            0 -> listOf(1, 2, 3)
            1 -> listOf(0, 2, 3)
            2 -> listOf(0, 1, 3)
            else -> listOf(0, 1, 2)
        }
        val adjColors = adj.map { tetra[it] }
        for (s in 0 until 3) {
            val rotated = adjColors.drop(3 - s) + adjColors.take(3 - s)
            val newPerm = listOf(tetra[i]) + rotated
            permutations.add(newPerm)
        }
    }
    return permutations
}

// Function to check if two tetrahedrons are identical under rotation
fun areTetrahedronsIdentical(tetraA: List<Int>, tetraB: List<Int>): Boolean {
    val aPerms = generatePermutations(tetraA)
    return tetraB in aPerms
}

assertions:
fun testTetrahedronIdentical() {
    // Test case 1: Identical tetrahedrons (simple rotation)
    assert(areTetrahedronsIdentical(listOf(1, 2, 3, 4), listOf(1, 3, 4, 2)))

    // Test case 2: Non-identical tetrahedrons
    assert(!areTetrahedronsIdentical(listOf(1, 2, 3, 4), listOf(1, 2, 4, 3)))

    // Test case 3: Identical tetrahedrons (another rotation)
    assert(areTetrahedronsIdentical(listOf(5, 6, 7, 8), listOf(5, 8, 6, 7)))

    // Test case 4: Non-identical tetrahedrons (different colors)
    assert(!areTetrahedronsIdentical(listOf(1, 1, 1, 1), listOf(1, 1, 1, 2)))
}

testTetrahedronIdentical()

