// Function to decode a phone number mnemonic to all possible phone numbers
fun decodePhoneMnemonic(mnemonic: String): List<String> {
    // Mapping of each letter to its corresponding digit on a phone keypad
    val digitMap = mapOf(
        'A' to "2", 'B' to "2", 'C' to "2",
        'D' to "3", 'E' to "3", 'F' to "3",
        'G' to "4", 'H' to "4", 'I' to "4",
        'J' to "5", 'K' to "5", 'L' to "5",
        'M' to "6", 'N' to "6", 'O' to "6",
        'P' to "7", 'Q' to "7", 'R' to "7", 'S' to "7",
        'T' to "8", 'U' to "8", 'V' to "8",
        'W' to "9", 'X' to "9", 'Y' to "9", 'Z' to "9"
    )

    // Convert each character in the mnemonic to its corresponding digit
    val phoneNumber = mnemonic.map { digitMap[it] ?: "" }.joinToString("")
    return listOf(phoneNumber)
}

assertions:
fun testDecodePhoneMnemonic() {
    // Test cases with expected results
