/**
 * Checks if a number can be expressed as the product of three distinct odd integers.
 *
 * @param n The number to check.
 * @return `true` if the number can be expressed as the product of three distinct odd integers, `false` otherwise.
 */
fun isProductOfThreeDistinctOdds(n: Int): Boolean {
    // Generate a list of odd numbers less than n
    val odds = (1 until n).filter { it % 2 != 0 }

    // Iterate through all combinations of three distinct odd numbers
    for (i in 0 until odds.size - 2) {
        for (j in i + 1 until odds.size - 1) {
            for (k in j + 1 until odds.size) {
                if (odds[i] * odds[j] * odds[k] == n) {
                    return true
                }
            }
        }
    }

    return false
}

