/**
 * This Kotlin program finds the smallest length of a contiguous subarray
 * whose sum is greater than or equal to a given target sum.
 * It uses a binary search approach combined with a sliding window technique
 * to efficiently determine the minimal length.
 */
fun findMinSubarrayLength(numbers: IntArray, targetSum: Int): Int {
    val n = numbers.size
    val prefixSum = IntArray(n + 1)

    // Compute prefix sums
    for (i in 1..n) {
        prefixSum[i] = prefixSum[i - 1] + numbers[i - 1]
    }

    var answer = 0
    var left = 1
    var right = n

    // Binary search for the minimal length
    while (left <= right) {
        val mid = (left + right) / 2
        var found = false

        // Check all possible subarrays of length 'mid'
        for (i in mid..n) {
            val currentSum = prefixSum[i] - prefixSum[i - mid]
            if (currentSum >= targetSum) {
                found = true
                break
            }
        }

        if (found) {
            answer = mid
            right = mid - 1
        } else {
            left = mid + 1
        }
    }

    return answer
}

assertions:
