    // Test case 1
    val numbers1 = intArrayOf(6, 18, 11, 13, 19, 11)
    val targetSum1 = 40
    assert(findMinSubarrayLength(numbers1, targetSum1) == 2)

    // Test case 2
    val numbers2 = intArrayOf(1, 4, 4, 7, 5, 6)
    val targetSum2 = 20
    assert(findMinSubarrayLength(numbers2, targetSum2) == 4)

    // Test case 3 (edge case: no subarray meets the target)
    val numbers3 = intArrayOf(1, 2, 3, 4, 5)
    val targetSum3 = 100
    assert(findMinSubarrayLength(numbers3, targetSum3) == 0)

    // Test case 4 (edge case: single element meets the target)
    val numbers4 = intArrayOf(10, 2, 3, 4, 5)
    val targetSum4 = 10
    assert(findMinSubarrayLength(numbers4, targetSum4) == 1)
}

