/**
 * Calculates the maximum growth between any two time points for each individual in a 2D array of heights.
 * The growth is defined as the difference between heights at two different time points.
 * 
 * @param heights A 2D array where each row represents an individual and each column represents a time point.
 * @return The maximum growth value found across all individuals and time point pairs.
 */
fun maxGrowth(heights: Array<IntArray>): Int {
    var maxGrowthValue = 0

    for (individualHeights in heights) {
        val timePoints = individualHeights.size
        for (t1 in 0 until timePoints) {
            for (t2 in t1 + 1 until timePoints) {
                val growth = individualHeights[t2] - individualHeights[t1]
                if (growth > maxGrowthValue) {
                    maxGrowthValue = growth
                }
            }
        }
    }

    return maxGrowthValue
}

