/**
 * Finds the length of the longest increasing subarray where the difference between consecutive elements is at most 'k'.
 *
 * @param n The length of the input array.
 * @param k The maximum allowed difference between consecutive elements in the increasing subarray.
 * @param array The input array of integers.
 * @return The length of the longest increasing subarray meeting the criteria.
 */
fun longestIncreasingSubarrayWithLimit(n: Int, k: Int, array: IntArray): Int {
    var maxLength = 1
    var currentLength = 1

    for (i in 1 until n) {
        if (array[i] > array[i - 1] && array[i] - array[i - 1] <= k) {
            currentLength++
            if (currentLength > maxLength) {
                maxLength = currentLength
            }
        } else {
            currentLength = 1
        }
    }

    return maxLength
}

