/**
 * Simulates a stack based on a series of operations.
 * The function processes each operation in the input list and maintains a stack accordingly.
 * The operations include PUSH, POP, TOP, and REVERSE-STACK.
 * 
 * @param operations A list of string operations to be performed on the stack.
 * @return A list of results from the TOP operations.
 */
fun stackSimulation(operations: List<String>): List<Any> {
    val stack = mutableListOf<Int>()
    val result = mutableListOf<Any>()

    for (operation in operations) {
        when {
            operation.startsWith("PUSH") -> {
                val x = operation.split(" ")[1].toInt()
                stack.add(x)
            }
            operation == "POP" -> {
                if (stack.isNotEmpty()) {
                    stack.removeAt(stack.size - 1)
                }
            }
            operation == "TOP" -> {
                if (stack.isNotEmpty()) {
                    result.add(stack.last())
                } else {
                    result.add("EMPTY")
                }
            }
            operation == "REVERSE-STACK" -> {
                stack.reverse()
            }
        }
    }

    return result
}

