/**
 * Modifies the list [lst] in-place to produce the next lexicographical permutation.
 * If no such permutation exists, it rearranges to the lowest possible order (sorted in ascending order).
 *
 * @param lst The list to modify in-place for the next permutation.
 */
fun nextPermutation(lst: MutableList<Int>) {
    val n = lst.size
    if (n <= 1) {
        return
    }

    // Step 1: Find the largest index k such that lst[k] < lst[k + 1].
    // If no such index exists, the permutation is the last permutation.
    var k = n - 2
    while (k >= 0 && lst[k] >= lst[k + 1]) {
        k--
    }

    if (k == -1) {
        // lst is sorted in descending order, rearrange to the lowest possible order (ascending)
        lst.reverse()
        return
    }

    // Step 2: Find the largest index l greater than k such that lst[k] < lst[l]
    var l = n - 1
    while (l > k && lst[l] <= lst[k]) {
        l--
    }

    // Step 3: Swap the value of lst[k] with that of lst[l]
    lst[k] = lst[l].also { lst[l] = lst[k] }

    // Step 4: Reverse the sequence from lst[k + 1] to the end
    lst.subList(k + 1, n).reverse()
}

