/**
 * Calculates the maximum sum of a non-empty subarray of a given length k.
 *
 * @param nums List of integers.
 * @param k Length of the subarray.
 * @return Maximum sum of a subarray of length k.
 */
fun maxSumSubarrayLengthK(nums: IntArray, k: Int): Int {
    if (nums.isEmpty() || k <= 0 || k > nums.size) {
        return 0
    }

    // Initial sum of the first subarray of length k
    var maxSum = nums.take(k).sum()
    var currentSum = maxSum

    // Sliding window to calculate the sum of remaining subarrays
    for (i in k until nums.size) {
        currentSum += nums[i] - nums[i - k]
        if (currentSum > maxSum) {
            maxSum = currentSum
        }
    }

    return maxSum
}

