    assert(maxSumSubarrayLengthK(intArrayOf(4, 2, 1, 7, 8, 1, 2, 8, 1, 0), 3) == 16)

    // Test case 3: All elements same
    assert(maxSumSubarrayLengthK(intArrayOf(1, 1, 1, 1, 1, 1, 1, 1), 4) == 4)

    // Test case 4: Negative numbers
