/**
 * Calculates the maximum sum of a non-empty subarray of a given length k.
 *
 * @param nums List of integers.
 * @param k Length of the subarray.
 * @return Maximum sum of a subarray of length k.
 */
fun maxSumSubarrayLengthK(nums: IntArray, k: Int): Int {
    if (nums.isEmpty() || k <= 0 || k > nums.size) {
        return 0
    }

    // Initial sum of the first subarray of length k
    var maxSum = nums.take(k).sum()
    var currentSum = maxSum

    // Sliding window to calculate the sum of remaining subarrays
    for (i in k until nums.size) {
        currentSum += nums[i] - nums[i - k]
        if (currentSum > maxSum) {
            maxSum = currentSum
        }
    }

    return maxSum
}

fun testMaxSumSubarrayLengthK() {
    // Test case 1: Normal case
    assert(maxSumSubarrayLengthK(intArrayOf(1, 2, 3, 4, 5), 2) == 9)

    // Test case 2: Larger array with obvious maximum
    assert(maxSumSubarrayLengthK(intArrayOf(4, 2, 1, 7, 8, 1, 2, 8, 1, 0), 3) == 16)

    // Test case 3: All elements same
    assert(maxSumSubarrayLengthK(intArrayOf(1, 1, 1, 1, 1, 1, 1, 1), 4) == 4)

    // Test case 4: Negative numbers
    assert(maxSumSubarrayLengthK(intArrayOf(-1, -2, -3, -4, -5), 2) == -3)

    // Test case 5: Invalid k value (0)
    assert(maxSumSubarrayLengthK(intArrayOf(10, 5, 2, 7, 8, 7), 0) == 0)

    // Additional edge case: k equals array length
    assert(maxSumSubarrayLengthK(intArrayOf(1, 2, 3), 3) == 6)

