/**
 * Calculates the minimum time required to reach a destination 16200 meters away,
 * considering different vehicles with their respective speeds and start times.
 * Vehicles with negative start times are ignored.
 *
 * @param vehicles List of pairs where each pair represents (speed in m/s, start time in seconds).
 * @return The minimum time in seconds, rounded up to the nearest integer, or null if no valid vehicles are provided.
 */
fun calculateMinTime(vehicles: List<Pair<Int, Int>>): Int? {
    var minTime: Double = Double.POSITIVE_INFINITY
    var hasValidVehicle = false

    for ((speed, startTime) in vehicles) {
        if (startTime < 0) {
            continue
        }
        hasValidVehicle = true
        val time = startTime + 16200.0 / speed
        if (time < minTime) {
            minTime = time
        }
    }

    return if (hasValidVehicle) ceil(minTime).toInt() else null
}

