import java.util.*
import kotlin.math.*

// Function to calculate factorial of a number
fun factorial(n: Int): Long {
    if (n <= 1) return 1
    var result = 1L
    for (i in 2..n) {
        result *= i
    }
    return result
}

// Function to check if a string can form a palindrome and calculate the number of distinct palindromic permutations
fun countPalindromePermutations(s: String): Long {
    val freq = HashMap<Char, Int>()
    for (c in s) {
        freq[c] = freq.getOrDefault(c, 0) + 1
    }

    // Check if it's possible to form a palindrome
    var oddCount = 0
    for (count in freq.values) {
        if (count % 2 != 0) {
            oddCount++
        }
    }
    val n = s.length
    if ((n % 2 == 0 && oddCount != 0) || (n % 2 == 1 && oddCount != 1)) {
        return 0
    }

    // Compute the factorial of half_length and divide by product of factorials of each char's half count
    val halfLength = n / 2
    var denominator = 1L
    for (count in freq.values) {
        denominator *= factorial(count / 2)
    }

    return factorial(halfLength) / denominator
}

