import java.util.PriorityQueue

// highestQualityPath finds the path from node 1 to node n with the maximum minimum quality.
// It uses Dijkstra's algorithm adapted to find the path where the minimum quality on the path is maximized.
// The graph is represented as an adjacency list, and the quality array contains the quality of each node.
fun highestQualityPath(n: Int, edges: List<Triple<Int, Int, Int>>, quality: IntArray): Int {
    // Create an adjacency list to represent the graph
    val graph = mutableMapOf<Int, MutableList<Pair<Int, Int>>>().withDefault { mutableListOf() }
    for ((u, v, w) in edges) {
        graph.getValue(u).add(Pair(v, w))
        graph.getValue(v).add(Pair(u, w))
    }

    // Use a max-heap (simulated with a min-heap by negating values) to store nodes by their current max min quality
    val pq = PriorityQueue<Pair<Int, Int>>(compareBy { it.first })
    pq.add(Pair(-quality[0], 1))

    // Track the maximum minimum quality found for each node
    val maxQuality = mutableMapOf(1 to quality[0])

    while (pq.isNotEmpty()) {
        val (currNegQuality, node) = pq.poll()
        val currQuality = -currNegQuality

        if (node == n) {
            return currQuality
        }

        for ((neighbor, _) in graph.getValue(node)) {
            val pathQuality = minOf(currQuality, quality[neighbor - 1])
            if (neighbor !in maxQuality || pathQuality > maxQuality.getValue(neighbor)) {
                maxQuality[neighbor] = pathQuality
                pq.add(Pair(-pathQuality, neighbor))
            }
        }
    }

    return -1
}

