// Function to simulate the growth of cells in a grid based on neighboring cells' values
fun simulateCellGrowth(grid: Array<IntArray>): Pair<Int, Array<IntArray>> {
    val n = grid.size
    val m = grid[0].size
    val currentGrid = grid.map { it.copyOf() }.toTypedArray()
    var time = 0

    while (true) {
        val toGrow = mutableListOf<Pair<Int, Int>>()

        for (i in 0 until n) {
            for (j in 0 until m) {
                val currentHeight = currentGrid[i][j]
                var found = false

                // Check up
                if (i > 0 && currentGrid[i - 1][j] == currentHeight + 1) {
                    found = true
                }
                // Check down
                else if (i < n - 1 && currentGrid[i + 1][j] == currentHeight + 1) {
                    found = true
                }
                // Check left
                else if (j > 0 && currentGrid[i][j - 1] == currentHeight + 1) {
                    found = true
                }
                // Check right
                else if (j < m - 1 && currentGrid[i][j + 1] == currentHeight + 1) {
                    found = true
                }

                if (found) {
                    toGrow.add(Pair(i, j))
                }
            }
        }

        if (toGrow.isEmpty()) {
            break
        }

        for ((i, j) in toGrow) {
            currentGrid[i][j] += 1
        }

        time += 1
    }

    return Pair(time, currentGrid)
}

