/**
 * This function calculates the number of tables required based on the left neighbor assignments.
 * Each guest points to another guest as their left neighbor, forming cycles. Each cycle represents one table.
 *
 * @param leftNeighbors An array where leftNeighbors[i] represents the left neighbor of guest i (1-based indexing).
 * @return The number of tables required.
 */
fun countTables(leftNeighbors: Array<Int>): Int {
    val n = leftNeighbors.size - 1  // because we're using 1-based indexing
    val visited = BooleanArray(n + 1) { false }
    var tables = 0

    for (guest in 1..n) {
        if (!visited[guest]) {
            tables++
            var current = guest
            while (!visited[current]) {
                visited[current] = true
                current = leftNeighbors[current]
            }
        }
    }

    return tables
}

