/**
 * Finds the maximum perimeter of a triangle that can be formed with any three sides from the given array.
 * 
 * @param arr An array of unique integers.
 * @return Maximum perimeter of the triangle or 0 if no valid triangle can be formed.
 */
fun maxPerimeter(arr: IntArray): Int {
    // Sort the array in non-decreasing order
    arr.sort()

    var maxPerimeter = 0

    // Iterate over the sorted array to find the maximum perimeter
    for (i in 0 until arr.size - 2) {
        val a = arr[i]
        val b = arr[i + 1]
        val c = arr[i + 2]

        // Check if a, b, and c can form a triangle
        if (a + b > c) {
            val perimeter = a + b + c
            maxPerimeter = maxOf(maxPerimeter, perimeter)
        }
    }

    return maxPerimeter
}

