// Function to check if the dice stacking is possible based on the given rules
fun isDiceStackingPossible(n: Int, x: Int, pairs: List<Pair<Int, Int>>): Boolean {
    var prevTop = x
    var prevBottom = 7 - prevTop
    var possible = true

    for (i in 0 until n) {
        val (a, b) = pairs[i]
        if (i == 0) {
            // For the first pair, just check if the initial top is valid
            if (a == prevBottom || b == prevBottom || a + b == 7) {
                possible = false
                break
            }
        } else {
            val validTopCandidates = mutableListOf<Int>()
            for (tCandidate in 1..6) {
                if (tCandidate == prevBottom) continue
                val aInLateral = (a != tCandidate && a != (7 - tCandidate))
                val bInLateral = (b != tCandidate && b != (7 - tCandidate))
                if (aInLateral && bInLateral && (a + b != 7)) {
                    validTopCandidates.add(tCandidate)
                }
            }
            when {
                validTopCandidates.size > 1 -> {
                    possible = false
                    break
                }
                validTopCandidates.isEmpty() -> {
                    possible = false
                    break
                }
                else -> {
                    prevTop = validTopCandidates[0]
                    prevBottom = 7 - prevTop
                }
            }
        }
    }
    return possible
}

