/**
 * Returns the largest integer k such that the sum of the first k natural numbers is less than or equal to n.
 * The sum of the first k natural numbers is calculated using the formula: sum = k*(k+1)/2.
 * This function iteratively increases k until the sum exceeds n.
 *
 * @param n The upper limit for the sum of the first k natural numbers.
 * @return The largest integer k satisfying the condition.
 */
fun largestK(n: Int): Int {
    var k = 0
    var total = 0
    while (total + k + 1 <= n) {
        k += 1
        total += k
    }
    return k
}

/**
 * Solves the problem for multiple test cases by applying the largestK function to each case.
 *
 * @param testCases An array of integers representing the upper limits for the sums.
 * @return An array of results, where each result is the largest k for the corresponding test case.
 */
fun solve(testCases: Array<Int>): Array<Int> {
    return testCases.map { largestK(it) }.toTypedArray()
}

