class TreeNode(var `val`: Int) {
    var left: TreeNode? = null
    var right: TreeNode? = null
}

// maxDepth calculates the maximum depth of a binary tree.
// The depth is defined as the number of nodes along the longest path from the root node down to the farthest leaf node.
// If the tree is empty (root is null), the depth is 0.
fun maxDepth(root: TreeNode?): Int {
    if (root == null) {
        return 0
    }

    val leftDepth = maxDepth(root.left)
    val rightDepth = maxDepth(root.right)

    return maxOf(leftDepth, rightDepth) + 1
}

