import java.util.*

/**
 * Finds all valid starting positions in the haystack array where the pattern matches.
 * 
 * @param n Size of the pattern array
 * @param m Size of the haystack array
 * @param p Pattern array (1-based indices)
 * @param h Haystack array to search in
 * @return List of valid 1-based starting positions
 */
fun findPatternPositions(n: Int, m: Int, p: List<Int>, h: List<Int>): List<Int> {
    val pZero = p.map { it - 1 }  // Convert to 0-based indices
    val result = mutableListOf<Int>()

    for (s in 0..m - n) {
        // Check for duplicate elements in current window
        val sub = h.subList(s, s + n)
        if (sub.toSet().size != n) {
            continue
        }

        // Check if sorted positions match pattern
        val sortedSub = sub.sorted()
        var valid = true
        for (i in 0 until n) {
            if (sortedSub[i] != h[s + pZero[i]]) {
                valid = false
                break
            }
        }

        if (valid) {
            result.add(s + 1)  // Convert back to 1-based position
        }
    }

    return result
}

