// Data class to represent a circle with center coordinates and squared radius (for efficient distance comparison)
data class Circle(val cx: Int, val cy: Int, val rSquared: Int)

// Function to count how many circles contain a given point
fun countContainingCircles(point: Pair<Int, Int>, circles: List<Circle>): Int {
    val (x, y) = point
    var count = 0
    for (circle in circles) {
        val dx = x - circle.cx
        val dy = y - circle.cy
        if (dx * dx + dy * dy < circle.rSquared) {
            count++
        }
    }
    return count
}

