import java.util.ArrayDeque

/**
 * Uses BFS to build level graph and store level of each node in level array.
 * Returns true if there is a path from source to sink, else false.
 */
fun bfsLevelGraph(capacity: Array<IntArray>, source: Int, sink: Int, level: IntArray): Boolean {
    level.fill(-1)
    level[source] = 0
    val queue = ArrayDeque<Int>()
    queue.add(source)

    while (queue.isNotEmpty()) {
        val u = queue.poll()
        for (v in capacity.indices) {
            if (level[v] < 0 && capacity[u][v] > 0) {
                level[v] = level[u] + 1
                queue.add(v)
            }
        }
    }
    return level[sink] >= 0
}

/**
 * Uses DFS to send flow from source to sink.
 * Returns the amount of flow sent.
 */
fun dfsFlow(
    u: Int,
    flow: Int,
    capacity: Array<IntArray>,
    sink: Int,
    level: IntArray,
    start: IntArray
): Int {
    if (u == sink) return flow

    for (v in start[u] until capacity.size) {
        if (capacity[u][v] > 0 && level[v] == level[u] + 1) {
            val currFlow = minOf(flow, capacity[u][v])
            val tempFlow = dfsFlow(v, currFlow, capacity, sink, level, start)
            if (tempFlow > 0) {
                capacity[u][v] -= tempFlow
                capacity[v][u] += tempFlow
                return tempFlow
            }
        }
        start[u]++
    }
    return 0
}

/**
 * Returns the maximum flow from source to sink in the given capacity graph
 * using Dinic's Algorithm.
 */
fun calculateMaxDeliveryFlow(capacity: Array<IntArray>, source: Int, sink: Int): Int {
    val n = capacity.size
    val level = IntArray(n)
    var maxFlow = 0

    while (bfsLevelGraph(capacity, source, sink, level)) {
        val start = IntArray(n)
        while (true) {
            val flow = dfsFlow(source, Int.MAX_VALUE, capacity, sink, level, start)
            if (flow == 0) break
            maxFlow += flow
        }
    }
    return maxFlow
}

