import kotlin.math.sqrt

/**
 * Solves the linear system Ax = b using the Conjugate Gradient method.
 *
 * @param A Symmetric positive definite matrix represented as a 2D array
 * @param b Right-hand side vector represented as a 1D array
 * @param x0 Initial guess for the solution (optional). If null, starts with a zero vector.
 * @param tol Tolerance for convergence (default is 1e-10)
 * @param maxIter Maximum number of iterations (optional). If null, defaults to the size of A.
 * @return Solution vector as a 1D array
 */
fun conjugateGradient(
    A: Array<DoubleArray>,
    b: DoubleArray,
    x0: DoubleArray? = null,
    tol: Double = 1e-10,
    maxIter: Int? = null
): DoubleArray {
    val n = b.size
    val x = x0?.copyOf() ?: DoubleArray(n) { 0.0 }

    var r = DoubleArray(n) { i -> b[i] - dotProduct(A[i], x) }
    var p = r.copyOf()
    var rsOld = dotProduct(r, r)

    val maxIterations = maxIter ?: n

    for (i in 0 until maxIterations) {
        val Ap = matrixVectorProduct(A, p)
        val alpha = rsOld / dotProduct(p, Ap)

        for (j in 0 until n) {
            x[j] += alpha * p[j]
            r[j] -= alpha * Ap[j]
        }

        val rsNew = dotProduct(r, r)
        if (sqrt(rsNew) < tol) {
            break
        }

        val beta = rsNew / rsOld
        for (j in 0 until n) {
            p[j] = r[j] + beta * p[j]
        }

        rsOld = rsNew
    }

    return x
}

/**
 * Computes the dot product of two vectors.
 */
private fun dotProduct(a: DoubleArray, b: DoubleArray): Double {
    require(a.size == b.size) { "Vectors must have the same length" }
    var sum = 0.0
    for (i in a.indices) {
        sum += a[i] * b[i]
    }
    return sum
}

/**
 * Multiplies a matrix by a vector.
 */
private fun matrixVectorProduct(matrix: Array<DoubleArray>, vector: DoubleArray): DoubleArray {
    require(matrix[0].size == vector.size) { "Matrix columns must match vector length" }
    return DoubleArray(matrix.size) { i -> dotProduct(matrix[i], vector) }
}

