// Function to calculate the average of an array of numbers, ignoring non-numeric values.
// If the input array contains no numeric values, it returns 0.0.
fun average(numbers: List<Any?>): Double {
    // Filter out non-numeric values and cast them to Double
    val numericValues = numbers.filterIsInstance<Number>().map { it.toDouble() }

    // Return 0.0 if there are no numeric values
    if (numericValues.isEmpty()) {
        return 0.0
    }

    // Calculate and return the average
    return numericValues.average()
}

