// Function to determine if a string is almost palindromic.
// A string is almost palindromic if it can become a palindrome by changing at most one character.
fun isAlmostPalindromic(s: String): String {
    var left = 0
    var right = s.length - 1
    var mismatchCount = 0

    while (left < right) {
        if (s[left] != s[right]) {
            mismatchCount++
            if (mismatchCount > 1) {
                return "NO"
            }
        }
        left++
        right--
    }

    return "YES"
}

