/**
 * Returns the maximum number of non-overlapping projects that can be scheduled.
 * Projects are represented as pairs of start and end times.
 * The function sorts the projects by their end times and then iterates through them,
 * selecting projects that do not overlap with the last selected project.
 *
 * @param projects List of projects, where each project is represented as a Pair<Int, Int> (start time, end time).
 * @return The maximum number of non-overlapping projects.
 */
fun maxNonOverlappingProjects(projects: List<Pair<Int, Int>>): Int {
    // Step 1: Sort the projects by their end times
    val sortedProjects = projects.sortedBy { it.second }

    // Step 2: Initialize variables to keep track of the maximum count of non-overlapping projects
    // and the end time of the last added project
    var maxCount = 0
    var lastEndTime = 0

    // Step 3: Iterate through the sorted projects
    for ((start, end) in sortedProjects) {
        // If the start time of the current project is greater than or equal to the end time of the last added project
        if (start >= lastEndTime) {
            // Include this project in the schedule
            maxCount += 1
            // Update the end time of the last added project
            lastEndTime = end
        }
    }

    return maxCount
}

