// Function to transform a square matrix based on the specified transformation type
fun transformMatrix(matrix: Array<IntArray>, transformationType: String): Array<IntArray> {
    val n = matrix.size

    // Rotates the matrix 90 degrees clockwise
    fun rotateClockwise(matrix: Array<IntArray>): Array<IntArray> {
        return Array(n) { i ->
            IntArray(n) { j ->
                matrix[n - 1 - j][i]
            }
        }
    }

    // Rotates the matrix 90 degrees counter-clockwise
    fun rotateCounterClockwise(matrix: Array<IntArray>): Array<IntArray> {
        return Array(n) { i ->
            IntArray(n) { j ->
                matrix[j][n - 1 - i]
            }
        }
    }

    // Inverts the top-left quadrant of the matrix
    fun topLeftInvert(matrix: Array<IntArray>): Array<IntArray> {
        val submatrix = matrix.take(n / 2).map { it.take(n / 2).toIntArray() }.toTypedArray()
        val inverted = submatrix.reversedArray().map { it.reversedArray() }.toTypedArray()
        for (i in 0 until n / 2) {
            for (j in 0 until n / 2) {
                matrix[i][j] = inverted[i][j]
            }
        }
        return matrix
    }

    // Inverts the bottom-left quadrant of the matrix
    fun bottomLeftInvert(matrix: Array<IntArray>): Array<IntArray> {
        val submatrix = matrix.takeLast(n / 2).map { it.take(n / 2).toIntArray() }.toTypedArray()
        val inverted = submatrix.reversedArray().map { it.reversedArray() }.toTypedArray()
        for (i in n / 2 until n) {
            for (j in 0 until n / 2) {
                matrix[i][j] = inverted[i - n / 2][j]
            }
        }
        return matrix
    }

    return when (transformationType) {
        "rotate_clockwise" -> rotateClockwise(matrix)
        "rotate_counterclockwise" -> rotateCounterClockwise(matrix)
        "top_left_invert" -> topLeftInvert(matrix)
        "bottom_left_invert" -> bottomLeftInvert(matrix)
        else -> throw IllegalArgumentException("Invalid transformation type")
    }
}

