/**
 * Finds the common intervals during which all employees are simultaneously available.
 *
 * @param intervals A list of intervals representing the working hours of employees.
 * Each interval is a list of two integers [start, end].
 * @return A list of common intervals when all employees are simultaneously available.
 * Returns an empty list if there are no common intervals.
 */
fun findCommonIntervals(intervals: List<List<Int>>): List<List<Int>> {
    if (intervals.isEmpty()) {
        return emptyList()
    }

    // Sort intervals by the start time
    val sortedIntervals = intervals.sortedBy { it[0] }

    // Initialize the common availability window as the first employee's interval
    var commonAvail = sortedIntervals[0].toMutableList()

    for (interval in sortedIntervals.subList(1, sortedIntervals.size)) {
        if (interval[0] > commonAvail[1] || interval[1] < commonAvail[0]) {
            // No overlap
            return emptyList()
        } else {
            commonAvail[0] = maxOf(commonAvail[0], interval[0])
            commonAvail[1] = minOf(commonAvail[1], interval[1])
        }
    }

    return listOf(commonAvail)
}

