import java.util.*

/**
 * Calculates the minimum number of stones needed to cover the required height difference.
 * The required height difference is calculated as (h - w).
 * If the required height difference is less than or equal to 0, no stones are needed.
 * Otherwise, the stones are sorted in descending order and used to cover the required height.
 * 
 * @param h The height to be achieved.
 * @param w The current height.
 * @param stones List of stones available to cover the height difference.
 * @return The number of stones needed to cover the height difference, or -1 if it's not possible.
 */
fun calculateStonesNeeded(h: Int, w: Int, stones: List<Int>): Int {
    val required = h - w
    if (required <= 0) {
        return 0
    }
    val sortedStones = stones.sortedDescending()
    var total = 0
    var count = 0
    for (stone in sortedStones) {
        total += stone
        count += 1
        if (total >= required) {
            return count
        }
    }
    return -1
}

