    assert(calculateStonesNeeded(10, 5, listOf(1, 2, 3, 4, 5)) == 2)

    // Test case 3: Not enough stones (h > w but stones are insufficient)
    assert(calculateStonesNeeded(10, 5, listOf(1, 1, 1)) == -1)

    // Test case 4: Exact stones needed (h - w = sum of stones)
    assert(calculateStonesNeeded(10, 5, listOf(2, 3)) == 2)
}

