import kotlin.math.sqrt

/**
 * Checks if a given number is square-free, meaning it is not divisible by any perfect square other than 1.
 * A number is square-free if in its prime factorization, no prime number appears more than once.
 *
 * @param n The number to check for being square-free.
 * @return `true` if the number is square-free, `false` otherwise.
 */
fun isSquareFree(n: Int): Boolean {
    if (n == 1) {
        return true
    }
    var num = n
    val maxDivisor = sqrt(num.toDouble()).toInt() + 1
    for (i in 2 until maxDivisor) {
        if (i * i > num) {
            break
        }
        var count = 0
        while (num % i == 0) {
            num /= i
            count++
            if (count >= 2) {
                return false
            }
        }
    }
    // The remaining num is either 1 or a prime number, which doesn't affect square-freeness
    return true
}

