/**
 * Finds the size of the largest continuous subarray whose sum is less than or equal to a given threshold.
 *
 * @param arr The array of integers to analyze.
 * @param k The maximum allowed sum for the subarray.
 * @return The size of the largest continuous subarray with sum <= k.
 */
fun largestSubarrayWithSumLessThanK(arr: IntArray, k: Int): Int {
    var maxLen = 0
    var currentSum = 0
    var start = 0

    for (end in arr.indices) {
        currentSum += arr[end]
        while (currentSum > k) {
            currentSum -= arr[start]
            start++
        }
        maxLen = maxOf(maxLen, end - start + 1)
    }

    return maxLen
}

