// Function to find the maximum sum of a subsequence where no two elements are both even or both odd consecutively.
// The function takes an array of integers and returns the maximum sum of such a subsequence.
fun findMaxAlternatingSum(arr: IntArray): Int {
    var maxSum = Int.MIN_VALUE
    var prevEven = Int.MIN_VALUE
    var prevOdd = Int.MIN_VALUE

    for (num in arr) {
        val currentEven: Int
        val currentOdd: Int

        if (num % 2 == 0) {
            currentEven = maxOf(num, if (prevOdd != Int.MIN_VALUE) prevOdd + num else Int.MIN_VALUE)
            currentOdd = Int.MIN_VALUE
        } else {
            currentOdd = maxOf(num, if (prevEven != Int.MIN_VALUE) prevEven + num else Int.MIN_VALUE)
            currentEven = Int.MIN_VALUE
        }

        val currentMax = maxOf(currentEven, currentOdd)
        if (currentMax > maxSum) {
            maxSum = currentMax
        }

        prevEven = currentEven
        prevOdd = currentOdd
    }

    return maxSum
}

