import java.util.*

/**
 * Calculates the length of the longest palindrome that can be built from the characters of the given string.
 * The string should contain only uppercase alphabets.
 *
 * @param s The input string containing uppercase alphabets.
 * @return The length of the longest possible palindrome.
 */
fun longestPalindrome(s: String): Int {
    // Count occurrences of each character
    val charCount = mutableMapOf<Char, Int>()
    for (char in s) {
        charCount[char] = charCount.getOrDefault(char, 0) + 1
    }

    var length = 0
    var oddCountPresent = false

    for (count in charCount.values) {
        // Add the even part of the count to length
        length += count / 2 * 2
        // Check if there is an odd count of characters
        if (count % 2 == 1) {
            oddCountPresent = true
        }
    }

    // If there's any character with an odd count, we can add one in the center of the palindrome
    if (oddCountPresent) {
        length += 1
    }

    return length
}

