import java.util.*

// findItinerary finds the itinerary (route) from 'JFK' airport using the given tickets.
// The tickets are represented as a list of pairs (from, to).
// The function returns the itinerary as a list of airports in the order they should be visited.
fun findItinerary(tickets: List<Pair<String, String>>): List<String> {
    // Build the adjacency list
    val adj = mutableMapOf<String, Deque<String>>()
    for ((from, to) in tickets) {
        adj.computeIfAbsent(from) { LinkedList() }.add(to)
    }

    // Sort each adjacency list lexicographically
    for (from in adj.keys) {
        adj[from] = LinkedList(adj[from]!!.sorted())
    }

    val stack = LinkedList<String>()
    stack.push("JFK")
    val result = mutableListOf<String>()

    while (stack.isNotEmpty()) {
        val current = stack.peek()
        if (adj.containsKey(current) && adj[current]!!.isNotEmpty()) {
            val nextNode = adj[current]!!.pollFirst()
            stack.push(nextNode)
        } else {
            result.add(stack.pop())
        }
    }

    // Reverse to get the correct route
    return result.reversed()
}

