/**
 * Calculates the total time required to complete N exercises based on the given parameters.
 * The function takes into account the initial and maximum heart rates, the increase per exercise,
 * and the decrease per rest period.
 *
 * @param N Target number of exercises to complete.
 * @param m Initial heart rate.
 * @param M Maximum heart rate.
 * @param T Heart rate increase per exercise.
 * @param R Heart rate decrease per rest period.
 * @return Total time required to complete N exercises, or -1 if it's impossible.
 */
fun calculateExerciseTime(N: Int, m: Int, M: Int, T: Int, R: Int): Int {
    var current = m
    var time = 0
    var exercise = 0

    if (m + T > M) {
        return -1
    }

    while (exercise < N) {
        if (current + T <= M) {
            current += T
            exercise += 1
            time += 1
        } else {
            current = maxOf(current - R, m)
            time += 1
            // Check for infinite loop condition where exercise is impossible
            if (current + T > M && current == m) {
                return -1
            }
        }
    }
    return time
}

