            current = maxOf(current - R, m)
            time += 1
            // Check for infinite loop condition where exercise is impossible
            if (current + T > M && current == m) {
                return -1
            }
        }
    }
    return time
}

fun testCalculateExerciseTime() {
    // Test case 1: Normal case
    assert(calculateExerciseTime(5, 70, 120, 25, 15) == 7)

    // Test case 2: Case where rest is needed but possible
    assert(calculateExerciseTime(100, 50, 100, 5, 200) == 100)

    // Test case 3: Case where only one exercise is possible
    assert(calculateExerciseTime(1, 60, 70, 11, 11) == 1)

    // Test case 4: Impossible case (initial heart rate + exercise > max)
    assert(calculateExerciseTime(200, 50, 200, 150, 1) == -1)

    // Test case 5: Complex case with multiple exercises and rests
    assert(calculateExerciseTime(19, 89, 143, 17, 13) == 87)
}

