// This Kotlin program simulates a grid operation where we update sub-rectangles of a grid
// and track how many cells match the target grid after each operation.

class GridOperations {
    companion object {
        fun processGridOperations(r: Int, c: Int, target: Array<Array<Int>>, queries: List<List<Int>>): List<Int> {
            val current = Array(r) { IntArray(c) }
            var count = 0
            val results = mutableListOf<Int>()

            // Initial count of cells where current matches target (only 0's initially)
            for (i in 0 until r) {
                for (j in 0 until c) {
                    if (target[i][j] == 0) {
                        count++
                    }
                }
            }

            // Process each query
            for (query in queries) {
                val r1 = query[0] - 1
                val r2 = query[1] - 1
                val c1 = query[2] - 1
                val c2 = query[3] - 1
                val x = query[4]

                // Update each cell in the specified rectangle
                for (i in r1..r2) {
                    for (j in c1..c2) {
                        val oldVal = current[i][j]
                        if (oldVal == target[i][j]) {
                            count--
                        }
                        current[i][j] = x
                        if (x == target[i][j]) {
                            count++
                        }
                    }
                }

                // Add the current count to results after each operation
                results.add(count)
            }

            return results
        }
    }
}

