// Function to find the length of the longest substring with all unique characters
fun longestUniqueSubstringLength(s: String): Int {
    // HashMap to store the last occurrence of each character
    val charMap = mutableMapOf<Char, Int>()
    var left = 0
    var maxLength = 0

    for (right in s.indices) {
        val currentChar = s[right]
        // If the character is already in the map, update the left pointer
        if (charMap.containsKey(currentChar)) {
            left = maxOf(left, charMap[currentChar]!! + 1)
        }
        // Update the last occurrence of the current character
        charMap[currentChar] = right
        // Update the maximum length found so far
        maxLength = maxOf(maxLength, right - left + 1)
    }

    return maxLength
}

