// Function to count duplicate course registrations for students
// Input: A list of pairs where each pair represents a student ID and a course name
// Output: The number of duplicate registrations found
fun countDuplicateRegistrations(registrations: List<Pair<String, String>>): Int {
    val studentCourses = mutableMapOf<String, MutableSet<String>>()
    var errors = 0

    for ((studentId, course) in registrations) {
        if (studentCourses.containsKey(studentId)) {
            if (studentCourses[studentId]!!.contains(course)) {
                errors++
            } else {
                studentCourses[studentId]!!.add(course)
            }
        } else {
            studentCourses[studentId] = mutableSetOf(course)
        }
    }

    return errors
}

