/**
 * Determines if three given points can be separated by either a vertical or horizontal line.
 * A vertical line can separate the points if all x-coordinates are distinct.
 * A horizontal line can separate the points if all y-coordinates are distinct.
 *
 * @param points A list of three points, where each point is represented as a Pair<Int, Int> (x, y).
 * @return "YES" if the points can be separated by a vertical or horizontal line, otherwise "NO".
 */
fun canSeparatePoints(points: List<Pair<Int, Int>>): String {
    val (x1, y1) = points[0]
    val (x2, y2) = points[1]
    val (x3, y3) = points[2]

    // Check for vertical cut (all x's distinct)
    if (x1 != x2 && x2 != x3 && x1 != x3) {
        return "YES"
    }
    // Check for horizontal cut (all y's distinct)
    else if (y1 != y2 && y2 != y3 && y1 != y3) {
        return "YES"
    }
    return "NO"
}

