// maxPlantsWatered calculates the maximum number of distinct plants a gardener can water
// starting from the top-left corner (0, 0) and moving in four directions (up, down, left, right)
// with a limited number of moves (k) on an m x n grid.
fun maxPlantsWatered(m: Int, n: Int, k: Int): Int {
    // If the number of moves is greater than or equal to cells in the grid, return total cells
    if (k >= m * n) {
        return m * n
    }

    // Nested DFS function to explore all possible moves
    fun dfs(x: Int, y: Int, remainingMoves: Int, visited: MutableSet<Pair<Int, Int>>): Int {
        // Base cases: out of bounds, no remaining moves, or already visited
        if (x < 0 || y < 0 || x >= m || y >= n || remainingMoves < 0 || visited.contains(x to y)) {
            return 0
        }

        visited.add(x to y)
        var maxPlants = 1

        // Explore all four directions
        val up = dfs(x - 1, y, remainingMoves - 1, visited.toMutableSet())
        val down = dfs(x + 1, y, remainingMoves - 1, visited.toMutableSet())
        val left = dfs(x, y - 1, remainingMoves - 1, visited.toMutableSet())
        val right = dfs(x, y + 1, remainingMoves - 1, visited.toMutableSet())

        maxPlants += maxOf(up, down, left, right)

        return maxPlants
    }

    return dfs(0, 0, k, mutableSetOf())
}

