    // Single row grid
    assert(maxPlantsWatered(1, 5, 4) == 5)
    // Single column grid
    assert(maxPlantsWatered(5, 1, 4) == 5)
    // Grid with exact moves needed to cover all cells
    assert(maxPlantsWatered(3, 3, 9) == 9)
}

