/**
 * This Kotlin program calculates the number of valid sequences of length `n` based on specific rules.
 * The rules are:
 * - For sequences of length 1, there are 3 possible sequences.
 * - For sequences of length 2, there are 9 possible sequences.
 * - For sequences of length > 2, the count is determined by dynamic programming where:
 *   - If the last two elements are the same, the next element can be any of the 3 options.
 *   - If the last two elements are different, the next element must be one of the two previous elements.
 */

fun calculateSequences(maxN: Int): IntArray {
    val ans = IntArray(maxN + 1) { 0 }
    ans[1] = 3
    if (maxN >= 2) {
        ans[2] = 9
    }
    if (maxN >= 3) {
        // Initialize DP for n=2, each pair (a,b) has count 1
        var prevDp = Array(3) { IntArray(3) { 1 } }
        for (k in 3..maxN) {
            val currDp = Array(3) { IntArray(3) { 0 } }
            for (a in 0 until 3) {
                for (b in 0 until 3) {
                    val cnt = prevDp[a][b]
                    if (cnt == 0) {
                        continue
                    }
                    if (a == b) {
                        for (c in 0 until 3) {
                            currDp[b][c] += cnt
                        }
                    } else {
                        for (c in listOf(a, b)) {
                            currDp[b][c] += cnt
                        }
                    }
                }
            }
            val currentSum = currDp.sumOf { row -> row.sum() }
            ans[k] = currentSum
            prevDp = currDp
        }
    }
    return ans
}

