// Function to calculate the maximum number of swords that can be made from given binary codes representing parts.
// The binary codes represent Top (T), Bottom (B), Left (L), and Right (R) parts of the swords.
// The function counts the number of '0's in each position (T, B, L, R) and calculates the maximum swords and remaining parts.
fun calculateSwordsAndRemainingParts(n: Int, lines: List<String>): Triple<Int, Int, Int> {
    var countT = 0
    var countB = 0
    var countL = 0
    var countR = 0

    for (line in lines) {
        if (line[0] == '0') countT++
        if (line[1] == '0') countB++
        if (line[2] == '0') countL++
        if (line[3] == '0') countR++
    }

    val totalTB = countT + countB
    val totalLR = countL + countR

    val maxSwords = minOf(totalTB / 2, totalLR / 2)
    val remainingTB = totalTB - 2 * maxSwords
    val remainingLR = totalLR - 2 * maxSwords

    return Triple(maxSwords, remainingTB, remainingLR)
}

// Function to find the side length of the largest square island in a 2D grid.
// A square island is a group of adjacent 1's (land) forming a square.
// The function uses dynamic programming to efficiently compute the largest square.
fun largestSquareIsland(grid: Array<IntArray>): Int {
    if (grid.isEmpty() || grid[0].isEmpty()) {
        return 0
    }

    val rows = grid.size
    val cols = grid[0].size
    var maxSide = 0

    // dp[i][j] represents the side length of the largest square ending at (i, j)
    val dp = Array(rows) { IntArray(cols) }

    for (i in 0 until rows) {
        for (j in 0 until cols) {
            if (grid[i][j] == 1) {
                if (i == 0 || j == 0) {
                    // The largest square ending at the first row or column is the cell itself if it is land
                    dp[i][j] = 1
                } else {
                    // Update dp value only if the current cell is land
                    dp[i][j] = minOf(dp[i - 1][j], dp[i][j - 1], dp[i - 1][j - 1]) + 1
                }
                maxSide = maxOf(maxSide, dp[i][j])
            }
        }
    }

    return maxSide
}

